<?php $total = 0;if(isset($_SESSION['carrito']) and count($_SESSION['carrito']) !=0 ){?><ul class="header-cart-wrapitem w-full"><?php foreach($_SESSION['carrito'] as $producto){$path= PUBLICDOCS.'/img/catalogo/';$total+= $producto['cantidad']*$producto['price'];$idP=openssl_encrypt($producto['id'],METHODENCRIPT,KEY);?><li class="header-cart-item flex-w flex-t m-b-12"><div class="header-cart-item-img" idpr="<?= $idP; ?>" op="1" onclick="dropItem(this)" sp="<?= $producto['id'];?>" tl="<?= $producto['talla'];?>" gc="<?= $producto['gender'];?>"><img src="<?= $path.$producto['photo']?>"></div><div class="header-cart-item-txt p-t-8"><a href="<?= URL_FULL.'/productos/producto/'.$producto['id'].'/'.$producto['url'];?>" class="font-semibold header-cart-item-name m-b-1 text-white hover:text-blue-700 trans-04"><?= $producto['titulo'];?></a>Talla: <input type="hidden" id="tl<?= $producto['id'].'-'.$producto['talla'];?>" value="<?= $producto['talla'];?>"><span><?= $producto['talla'];?></span> - <input type="hidden" id="gd<?= $producto['id'].'-'.$producto['gender'];?>" value="<?= $producto['gender'];?>"><span><?= ucwords($producto['gender']);?></span><span class="header-cart-item-info text-white"><?= $producto['cantidad']. ' x '. SMONEY.formatMoney($producto['price']);?></span></div></li><?php } ?></ul><div class="w-full"><div class="header-cart-total w-full p-tb-40 text-white">Total: <?= SMONEY.formatMoney($total);?></div><div class="header-cart-buttons flex-w w-full"><a href="<?= URL_FULL; ?>/carrito" class="flex-c-m text-lg cl0 size-107 uppercase text-white bg-gradient-to-r from-blue-700 to-blue-500 hover:bg-gradient-to-bl focus:ring-4 focus:outline-none focus:ring-blue-700 dark:focus:ring-blue-500 rounded-lg m-r-8 mb-2">Ver Carrito</a><a href="<?= URL_FULL;?>/carrito/pagar" class="flex-c-m text-lg cl0 size-107 uppercase text-white bg-gradient-to-r from-blue-700 to-blue-500 hover:bg-gradient-to-bl focus:ring-4 focus:outline-none focus:ring-blue-700 dark:focus:ring-blue-500 rounded-lg mb-2">Pagar</a></div></div><?php /*fin carrito con productos*/  }else{?><div class="text-center items-center max-w-sm p-6 bg-white"><img class="my-2" src="https://www.svgrepo.com/show/17356/empty-cart.svg" width="80" height="auto"><h5 class="my-2 mb-2 text-2xl font-semibold tracking-tight text-gray-900 dark:text-white">Carrito vacio :( </h5></div><?php }?>